import React, {memo, useState} from "react";

const Todo = memo(props =>{
    const { todo, getTodoEditingId, todoEditingId, onEditTodo, index } = props
    const [text, setText] = useState(todo.text)
    const isEditing = todoEditingId === todo.id
    const editTodo = () => {
        onEditTodo({
            ...todo,
            text
        }, index)
    }
    return(
            // className={'${isEditing ? 'editing' : ''} ${todo.isCompleted ? 'completed' : ''}'} >
            <li className={isEditing ? 'editing' : '' & todo.isCompleted ? 'completed' : ''} >
            {!isEditing ?
                <div className="view">
                    <input className="toggle" type="checkbox" checked= {todo.isCompleted} />
                    <label onDoubleClick={ () =>getTodoEditingId(todo.id)} >{todo.text}</label>
                    <button className="destroy"></button>
                </div> :
                <input 
                    className="edit" 
                    type="text" 
                    value={text}
                    onChange={e => setText(e.target.value)}
                />
                }
        </li>
    )
})

export default Todo
