//components
import Header from './components/Header';
import Todolist from './components/Todolist';
import Footer from './components/Footer';

//css
import './App.css';
import './css/Todo.css'


class App extends React.PureComponent {
  state = {
    todosList: [{
      id: 1,
      text: 'todo1',
      isCompleted: true
    },
    {
      id: 2,
      text: 'todo2',
      isCompleted: false
    }]
  }
  render(){
    const { todosList } = this.state
    return (
      <div className="todoapp">

          <Header/>
          <Todolist/>
          <Footer/>
      </div>
    );
  }

}

export default App;
