import React, {memo, useState} from "react";

const Header = memo(() => {
    const [text, setText] = useState('')
    return (
        <header className="header">
            <h1>TodoApp</h1>
            <input 
                className="new-todo" 
                value={text}
                onChange={(e) => setText(e.target.value)}
            />
        </header>
    )
})
export default Header
